//ValidateInput.js

import React from 'react';

export const validateUserInput = (userInput) => {
	const errors = {}; //creates an error object that stores errors of user input
	var isValid=true;

	const companyLength = userInput.company.length
	const positionLength = userInput.position.length
	const locationLength = userInput.location.length
	const urlLength = userInput.url.length

	if (companyLength == 0)
	{
		errors.company = "Enter a company name"
	}

	else if (companyLength > 40)
	{
		errors.company = "Company must be 40 characters or less"
	}

	return errors; 
};
