//JobsList.js
import React, { useState, useEffect } from 'react';
import { getJobs } from './utils/jobs_list/GetJobs.js'

const JobsList = () => {
	const [jobs, setJobs] = useState([]);

	useEffect(() => {
    		getJobs().then(data => setJobs(data));
  	}, []);

	return (
    		<div className="box p-0">
      			<div className="table-container jobs-table-container">
        			<table className="table is-fullwidth is-striped is-hoverable">
          				<thead>
            					<tr>
              						<th>Company</th>
              						<th>Position</th>
              						<th>Status</th>
              						<th>Date</th>
              						<th className="jobs-actions">Actions</th>
            					</tr>
          				</thead>
          				<tbody>
            					{jobs.map(job => (
              						<tr key={job.id}>
                						<td>{job.company}</td>
                						<td>{job.position}</td>
                						<td>
                  							<span className={`tag ${job.status}`}>
                    								{job.status}
                  							</span>
                						</td>
                						<td>{new Date(job.date).toLocaleDateString()}</td>
                						<td className="jobs-actions">
                  							<button className="button is-small is-info">
                    								<i className="fas fa-edit"></i>
                  							</button>
                  							<button className="button is-small is-danger">
                    								<i className="fas fa-trash"></i>
                  							</button>
                						</td>
              						</tr>
            					))}
          				</tbody>
        			</table>
      			</div>
    		</div>
  	);	

	/*
	return (
    		<div className="jobs-container">
      			{jobs && jobs.length > 0 ? (
        			<ul className="jobs-list">
          				{jobs.map(job => (
            					<li key={job.id} className="job-item">
              						<div>
                						<strong>{job.company}</strong> - {job.position}
              						</div>
              						<span className="job-status">{job.status}</span>
            					</li>
          				))}
        			</ul>
      			) : (
        		<p>No jobs found</p>
      			)}
    		</div>
  	);	
	*/
};

export default JobsList;
