import React, { useState, useEffect } from 'react';
import Portal from './StatusDropdownPortal';

const JobsList = ({ jobsState }) => {
  const [openDropdownId, setOpenDropdownId] = useState(null);
  const [dropdownCoords, setDropdownCoords] = useState({ top: 0, left: 0 });

  const toggleDropdown = (e, jobId) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    if (openDropdownId === jobId) {
      setOpenDropdownId(null);
      return;
    }
    setDropdownCoords({ top: rect.bottom, left: rect.left });
    setOpenDropdownId(jobId);
  };

  useEffect(() => {
    const handleClickOutside = () => setOpenDropdownId(null);
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <table>
      <tbody>
        {jobsState.map(job => (
          <tr key={job.id}>
            <td>{job.company}</td>
            <td>{job.position}</td>
            <td>
              <button onClick={(e) => toggleDropdown(e, job.id)}>Actions â–¼</button>
              {openDropdownId === job.id && (
                <Portal>
                  <div
                    style={{
                      position: 'fixed',
                      top: dropdownCoords.top,
                      left: dropdownCoords.left,
                      background: 'white',
                      border: '1px solid #ccc',
                      padding: '8px',
                      zIndex: 9999,
                      boxShadow: '0 2px 6px rgba(0,0,0,0.2)',
                      borderRadius: 4,
                    }}
                    onClick={e => e.stopPropagation()}
                  >
                    <div>
                      <a href="#!" onClick={() => alert('Rejected clicked')}>Rejected</a><br />
                      <a href="#!" onClick={() => alert('Interview clicked')}>Interview</a><br />
                      <a href="#!" onClick={() => alert('Delete clicked')}>Delete</a>
                    </div>
                  </div>
                </Portal>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default JobsList;
