import { useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';

const Portal = ({ children }) => {
  const portalRef = useRef(null);

  useEffect(() => {
    // Create portal root if it doesn't exist
    const portalRoot = document.getElementById('portal-root');
    if (!portalRoot) {
      const newPortalRoot = document.createElement('div');
      newPortalRoot.id = 'portal-root';
      newPortalRoot.style.position = 'fixed';
      newPortalRoot.style.top = '0';
      newPortalRoot.style.left = '0';
      newPortalRoot.style.zIndex = '10000';
      document.body.appendChild(newPortalRoot);
      portalRef.current = newPortalRoot;
    } else {
      portalRef.current = portalRoot;
    }
  }, []);

  if (typeof window === 'undefined' || !portalRef.current) return null;

  return createPortal(children, portalRef.current);
};

export default Portal;
