import React, { useState, useEffect } from 'react';

const JobsList = ({ jobsState }) => {
  const [openDropdownId, setOpenDropdownId] = useState(null);
  const [dropdownCoords, setDropdownCoords] = useState({ top: 0, left: 0 });

  const toggleDropdown = (e, jobId) => {
    e.stopPropagation();
    console.log('toggleDropdown called for jobId:', jobId);
    const rect = e.currentTarget.getBoundingClientRect();
    console.log('dropdown coords:', rect.bottom, rect.left);
    if (openDropdownId === jobId) {
      setOpenDropdownId(null);
      return;
    }
    setDropdownCoords({ top: rect.bottom, left: rect.left });
    setOpenDropdownId(jobId);
  };

  useEffect(() => {
    const handleClickOutside = () => {
      setOpenDropdownId(null);
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <>
      <div style={{ marginBottom: 10 }}>
        Open Dropdown ID: {openDropdownId || 'none'}
      </div>
      <table>
        <tbody>
          {jobsState.map(job => (
            <tr key={job.id}>
              <td>{job.company}</td>
              <td>
                <button onClick={e => toggleDropdown(e, job.id)}>Actions â–¼</button>
                {openDropdownId === job.id && (
                  <div
                    style={{
                      position: 'fixed',
                      top: dropdownCoords.top,
                      left: dropdownCoords.left,
                      backgroundColor: 'white',
                      border: '1px solid black',
                      padding: 8,
                      zIndex: 9999,
                    }}
                    onClick={e => e.stopPropagation()}
                  >
                    Dropdown content for {job.company}
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default JobsList;
