// JobsList.js
import React, { useState, useEffect } from 'react';
import { deleteJob } from './utils/jobs_list/DeleteJob.js';
import Portal from './StatusDropdownPortal.js';

const JobsList = ({ removeJobCallBack, jobsState }) => {
  const [openDropdownId, setOpenDropdownId] = useState(null);
  const [dropdownCoords, setDropdownCoords] = useState({ top: 0, left: 0 });

  const toggleDropdown = (e, jobId) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    // If clicking the same dropdown, close it
    if (openDropdownId === jobId) {
      setOpenDropdownId(null);
      return;
    }
    // Position dropdown below the button
    setDropdownCoords({ top: rect.bottom, left: rect.left });
    setOpenDropdownId(jobId);
  };

  useEffect(() => {
    const handleClickOutside = () => {
      setOpenDropdownId(null);
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  const handleDeleteJob = (e, jobId, jobCompany, jobPosition) => {
    e.preventDefault();
    deleteJob(jobId, jobCompany, jobPosition);
    removeJobCallBack(jobId);
    setOpenDropdownId(null); // close dropdown after deleting
  };

  const countJobStatus = () => {
    const appliedCount = jobsState.filter(job => job.status === 'Applied').length;
    const rejectedCount = jobsState.filter(job => job.status === 'Rejected').length;
    const interviewCount = jobsState.filter(job => job.status === 'Interview').length;

    return { applied: appliedCount, rejected: rejectedCount, interview: interviewCount };
  };

  return (
    <div className="box p-0" style={{ marginTop: '20px' }}>
      <div className="table-container jobs-table-container">
        <table className="table is-fullwidth is-striped is-hoverable">
          <thead>
            <tr className="has-text-centered">
              <th colSpan="7" className="has-text-centered">
                Applied Jobs: {countJobStatus().applied}
              </th>
            </tr>
            <tr>
              <th style={{ width: '15%' }}>Company</th>
              <th style={{ width: '15%' }}>Position</th>
              <th style={{ width: '10%' }}>Date</th>
              <th style={{ width: '10%' }}>Status</th>
              <th style={{ width: '20%' }}>Location</th>
              <th style={{ width: '10%' }}>URL</th>
              <th style={{ width: '20%' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {jobsState.map(job => (
              <tr key={job.id}>
                <td>{job.company}</td>
                <td>{job.position}</td>
                <td className="jobs-table-narrow-column">{job.date}</td>
                <td className="jobs-table-narrow-column">
                  <span className={`tag ${job.status}`}>{job.status}</span>
                </td>
                <td>{job.location}</td>
                <td className="jobs-table-narrow-column">
                  <a href={job.url} target="_blank" rel="noopener noreferrer">
                    link
                  </a>
                </td>
                <td className="jobs-actions" style={{ position: 'relative' }}>
                  <button className="button" onClick={e => toggleDropdown(e, job.id)}>
                    Actions â–¼
                  </button>

                  {openDropdownId === job.id && (
                    <Portal>
                      <div
                        className="dropdown-menu is-active"
                        style={{
                          position: 'fixed',
                          top: dropdownCoords.top,
                          left: dropdownCoords.left,
                          zIndex: 9999,
                          backgroundColor: 'white',
                          border: '1px solid #ccc',
                          borderRadius: '4px',
                          boxShadow: '0 2px 6px rgba(0,0,0,0.2)',
                          minWidth: 120,
                          padding: 8,
                        }}
                        onClick={e => e.stopPropagation()}
                      >
                        <div
                          className="dropdown-item"
                          onClick={e => {
                            e.stopPropagation();
                            alert('Rejected clicked');
                            setOpenDropdownId(null);
                          }}
                          style={{ cursor: 'pointer' }}
                        >
                          Rejected
                        </div>
                        <div
                          className="dropdown-item"
                          onClick={e => {
                            e.stopPropagation();
                            alert('Interview clicked');
                            setOpenDropdownId(null);
                          }}
                          style={{ cursor: 'pointer' }}
                        >
                          Interview
                        </div>
                        <div
                          className="dropdown-item"
                          onClick={e =>
                            handleDeleteJob(e, job.id, job.company, job.position)
                          }
                          style={{ cursor: 'pointer', color: 'red' }}
                        >
                          Delete
                        </div>
                      </div>
                    </Portal>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default JobsList;
