//JobsList.js
import React, { useState, useEffect } from 'react';
import { deleteJob } from './utils/jobs_list/DeleteJob.js'

const JobsList = ({ removeJobCallBack, jobsState }) => {

	const handleDeleteJob = (e, jobId, jobCompany, jobTitle) => {
		e.preventDefault();
		deleteJob(jobId, jobCompany, jobTitle); //Delete job from the database
		removeJobCallBack(jobId); //RemoveJob from the jobs state
	};

	const countJobStatus = () => {
		const appliedCount = jobsState.filter(job => job.status === 'Applied').length;
		const rejectedCount = jobState.filter(job => job.status === 'Rejected').length;
		const interviewCount = jobState.filter(job => job.status === 'Interview').length;

		const statusCount = {
			applied: appliedCount,
			rejected: rejectedCount,
			interview: interviewCount
		};

		return statusCount
	};


        return (
                <div className="box p-0" style={{ marginTop: '20px' }}>
                        <div className="table-container jobs-table-container">
                                <table className="table is-fullwidth is-striped is-hoverable">
                                        <thead>
                				<tr className='has-text-centered'>
                                                	<th colSpan="7" className="has-text-centered">Applied Jobs:{countJobStatus().applied}</th>
						</tr>


						<tr>
                                                        <th style={{ width: '15%' }} >Company</th>
                                                        <th style={{ width: '15%' }} >Position</th>
                                                        <th style={{ width: '10%' }} >Date</th>
                                                        <th style={{ width: '10%' }} >Status</th>
                                                        <th style={{ width: '20%' }} >Location</th>
                                                        <th style={{ width: '10%' }} >URL</th>
                                                        <th style={{ width: '20%' }} >Actions</th>
                                                </tr>
                                        </thead>

                                        <tbody>
                                                {jobsState.map(job => (
                                                        <tr key={job.id}>
                                                                <td>{job.company}</td>
                                                                <td>{job.position}</td>
                                                                <td class='jobs-table-narrow-column'>{job.date}</td>
                                                                <td class='jobs-table-narrow-column'> <span className={`tag ${job.status}`}> {job.status} </span> </td>
                                                                <td>{job.location}</td>
                                                                <td class='jobs-table-narrow-column'> <a href={job.url} target='_blank' rel='noopener noreferrer' >link</a> </td> {/*target='_blank' opens link in new tab rel is for security*/}

                                                                <td className="jobs-actions">
                                                                        <button className="button is-small is-info">
                                                                                <i className="fas fa-edit">Rejected</i>
                                                                        </button>

									<button className="button is-small is-info">
										<i className = "fas fa-edit">Interview</i>
									</button>

                                                                        <button onClick={(e) => handleDeleteJob(e, job.id, job.company, job.title)} className="button is-small is-danger">
                                                                                <i className="fas fa-trash">delete</i>
                                                                        </button>
                                                                </td>
                                                        </tr>
                                                ))}
                                        </tbody>
                                </table>
                        </div>
                </div>
        );
};

export default JobsList;
